const init = [];

const reducer = (state = { items: init, load: true }, action) => {
    switch(action.type){
    case 'LOADING':
    return {
        
    };
    case 'ITEM_LOADED': 
    return {
        items: action.payload,
        load: false
    };
    default: 
    return state;
}
}

export default reducer;
