const itemLoaded = (item) =>{
    return {
        type: 'ITEM_LOADED',
        payload: item
            };
        };
const loading = () =>{
    return {
        type: 'LOADING'
    }
}
const error = () =>{
    return {
        type: 'ERROR'
        
    }
}
export {
    itemLoaded,
    loading
}
