const init = [
    {
        key: 1,
        id: 1,
        name: 'XiaomI mi Note',
        brand: 'Xiaomi'
},
    {
        key: 2,
        id: 2,
        name: "Lenovo Note",
        brand: "Lenovo"

    }
]
const reducer = (state = { items: init }, action) => {
    switch(action.type){
    case 'ITEM_LOADED': 
    return {
        items: action.payload
    };
    default: 
    return state;
}
}

export default reducer;
