import React, {Component} from 'react';
import ItemListDetail from '../item-list-detail';
import { connect } from 'react-redux';
import withService from '../hoc';
class ItemList extends Component{
    componentDidMount(){
        const {marketService} = this.props;
        console.log(marketService)
    }

    render(){
    const {items} = this.props;
    return(
        <ul>
            {
                items.map(i=>(
                    <li key={i.id}>
                        <ItemListDetail item={i}/>
                    </li>
                ))
            }
        </ul>
    )
  
}
}

const mapStateToProps = ({items}) => { 
    return {items};
}

const childConnect = connect(mapStateToProps)(ItemList);
//firstly, wrapp item-list component in context-function for connecting with redux;
export default withService()(childConnect);
// and then wrapp
