import React, {Component} from 'react';
import ItemListDetail from '../item-list-detail';
import { connect } from 'react-redux';
 ItemList = () => {
    const {marketService, items} = this.props;

    // useEffect(() =>{
    //   const data = marketService.getItem();
    //   console.log(data);
    // });
    return(
        <ul>
            {
                items.map(i=>(
                    <li key={i.id}>
                        <ItemListDetail item={i}/>
                    </li>
                ))
            }
        </ul>
    )
  
}

const mapStateToProps = ({items}) => { 
    return {items};
}

const childConnect = connect(mapStateToProps)(ItemList)
export default childConnect;
